<script type="text/javascript">
      function ConfirmadosMunicipales(obj){
            if(obj.value!="-"){
                  $("#confirmados-municipales").attr("href","<?php echo __VIRTUAL_DIRECTORY__?>/reporte.php?tipo=1&partido="+obj.value);
                  $("#confirmados-municipales").css("display","");
            }else{
                  $("#confirmados-municipales").css("display","none");
            }

      }
      function MapeoMunicipales(obj){
            if(obj.value!="-"){
                  $("#mapeo-municipales").attr("href","<?php echo __VIRTUAL_DIRECTORY__?>/reporte.php?tipo=2&partido="+obj.value);
                  $("#mapeo-municipales").css("display","");
            }else{
                  $("#mapeo-municipales").css("display","none");
            }

      }
